import React, { useEffect, useRef } from 'react';
import paper from 'paper';

// マウスポインターの座標
export class Vertex {
  x: number;
  y: number;
}

const Canvas = () => {
  const canvasRef = useRef(null);
  useEffect(() => {
    // canvasが準備できたらpaper.jsを初期化
    if (canvasRef.current) {
      paper.setup(canvasRef.current);
      // ここに描画処理を書く
      const circle = new paper.Path.Circle({
        center: [50, 50],
        radius: 30,
        strokeColor: 'black',
      });
      // 任意で、交差判定などの処理もここで行えます。
      console.log(circle);
    }
    // クリーンアップ処理 (コンポーネントがアンマウントされた時)
    return () => {
      paper.project.clear();  // 既存のpaper.jsプロジェクトをクリア
    };
  }, []);
  return (
    <div style={{ display: 'flex', flexDirection: 'row' }}>
      <canvas ref={canvasRef} width={600} height={500} style={{ border: '1px solid black' }} />
      <div style={{ display: 'flex', flexDirection: 'column', marginLeft: '10px' }}>
        <button onClick={toggleDrawing} className={isDrawing ? "bg-blue-500" : ""}>
          {isDrawing ? 'Stop Drawing' : 'Start Drawing'}
        </button>
        <button onClick={toggleErasing} className={isErasing ? "bg-yellow-500" : ""}>
          {isErasing ? 'Stop Erasing' : 'Start Erasing'}
        </button>
        <button onClick={deleteLine} disabled={selectedLine === null}>
          Delete Selected Line
        </button>
      </div>
    </div>
  );
};

export default Canvas;
